def genMaskandW(cap,list_red, list_valley,r,channel1,channel2 = None,channel3 = None):
    m = 0
    n = 1
    list_w_channel1 = np.zeros(len(list_red))
    if(channel2 != None):
        list_w_channel2 = np.zeros(len(list_red))
    if (channel3 != None):
        list_w_channel3 = np.zeros(len(list_red))
    # print("max      maxnum      min     minnum")
    for j in range(len(list_valley) - 1):
        max = 0
        index_max = 0
        min = 10000
        index_min = 0
        for i in range(list_valley[m], list_valley[n] + 1):
            if list_red[i] > max:
                max = list_red[i]
                index_max = i
            if list_red[i] < min:
                min = list_red[i]
                index_min = i
        # print(index_max, list_red[index_max], index_min, list_red[index_min])
        cap.set(cv2.CAP_PROP_POS_FRAMES, index_max)
        ret, frame = cap.read()
        if (ret == False):
            print("read frame Error! -1")
            exit(-1)
        mat_max = np.mat(frame[:, :, 2])
        cap.set(cv2.CAP_PROP_POS_FRAMES, index_min)
        ret, frame = cap.read()
        if (ret == False):
            print("read frame Error! -2")
            exit(-1)
        mat_min = np.mat(frame[:, :, 2])
        dif = mat_max - mat_min
        # print(dif1)
        mat_mask = (mat_max - mat_min > r) +0
        # print(Mask)
        # input()
        for i in range(list_valley[m], list_valley[n]+1):
            cap.set(cv2.CAP_PROP_POS_FRAMES, i)
            ret, frame = cap.read()
            if (ret == False):
                print("read frame Error! -3")
                exit(-1)
            mat_c_channel1 = np.mat(frame[:, :, channel1])
            list_w_channel1[i] = np.sum(np.multiply(mat_c_channel1, mat_mask)) / np.sum(mat_mask)
            if(channel2!= None):
                mat_c_channel2 = np.mat(frame[:, :, channel2])
                list_w_channel2[i] = np.sum(np.multiply(mat_c_channel2, mat_mask)) / np.sum(mat_mask)
            if (channel3 != None):
                mat_c_channel3 = np.mat(frame[:, :, channel3])
                list_w_channel3[i] = np.sum(np.multiply(mat_c_channel3, mat_mask)) / np.sum(mat_mask)
        if n != len(list_valley):
            m += 1
            n += 1
    print(n)
    if(channel2 != None and channel3 != None):
        return  list_w_channel1,list_w_channel2,list_w_channel3
    elif channel2 != None:
        return list_w_channel1, list_w_channel2
    elif channel3 != None:
        return list_w_channel1, list_w_channel3
    else:
        return list_w_channel1


